import React, { useState, useEffect } from 'react';
import { Plus, ArrowLeft, ArrowRight, Calendar, Clock, User, Edit, Trash2 } from 'lucide-react';
import { appointmentService, customerService, serviceService } from '../api/api';

const AppointmentsPage = () => {
  const [appointments, setAppointments] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [services, setServices] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editingAppointment, setEditingAppointment] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [formData, setFormData] = useState({
    customerId: '',
    serviceId: '',
    appointmentDate: '',
    agreedPrice: '',
    totalSessions: 1,
    remainingSessions: 1,
    status: 'Scheduled'
  });

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  const statusOptions = [
    { value: 'Scheduled', label: 'Scheduled', color: 'bg-blue-100 text-blue-800' },
    { value: 'Confirmed', label: 'Confirmed', color: 'bg-green-100 text-green-800' },
    { value: 'Completed', label: 'Completed', color: 'bg-gray-100 text-gray-800' },
    { value: 'Cancelled', label: 'Cancelled', color: 'bg-red-100 text-red-800' },
    { value: 'NoShow', label: 'No Show', color: 'bg-orange-100 text-orange-800' }
  ];

  // Sayfa yüklendiğinde verileri getir
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [appointmentsRes, customersRes, servicesRes] = await Promise.all([
        appointmentService.getAll(),
        customerService.getAll(),
        serviceService.getAll()
      ]);
      
      setAppointments(appointmentsRes.data);
      setCustomers(customersRes.data);
      setServices(servicesRes.data);
    } catch (error) {
      console.error('Veri yüklerken hata:', error);
      alert('Veriler yüklenirken bir hata oluştu');
    } finally {
      setIsLoading(false);
    }
  };

  // Takvim günlerini hesapla
  const getCalendarDays = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    
    const firstDayOfMonth = new Date(year, month, 1);
    const lastDayOfMonth = new Date(year, month + 1, 0);
    const startDate = new Date(firstDayOfMonth);
    startDate.setDate(startDate.getDate() - firstDayOfMonth.getDay());
    
    const days = [];
    for (let i = 0; i < 42; i++) {
      const date = new Date(startDate);
      date.setDate(startDate.getDate() + i);
      days.push({
        date: date,
        isCurrentMonth: date.getMonth() === month,
        isToday: date.toDateString() === new Date().toDateString()
      });
    }
    return days;
  };

  // Belirli bir güne ait randevuları getir
  const getAppointmentsForDay = (date) => {
    return appointments.filter(apt => {
      const aptDate = new Date(apt.appointmentDate);
      return aptDate.toDateString() === date.toDateString();
    });
  };

  // Ay değiştirme
  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  // Modal aç
  const openAddModal = (date = null) => {
    setEditingAppointment(null);
    const appointmentDateTime = date 
      ? `${date.toISOString().split('T')[0]}T09:00`
      : '';
    
    setFormData({
      customerId: '',
      serviceId: '',
      appointmentDate: appointmentDateTime,
      agreedPrice: '',
      totalSessions: 1,
      remainingSessions: 1,
      status: 'Scheduled'
    });
    setSelectedDate(date);
    setShowModal(true);
  };

  const openEditModal = (appointment) => {
    setEditingAppointment(appointment);
    setFormData({
      customerId: appointment.customerId.toString(),
      serviceId: appointment.serviceId.toString(),
      appointmentDate: new Date(appointment.appointmentDate).toISOString().slice(0, 16),
      agreedPrice: appointment.agreedPrice.toString(),
      totalSessions: appointment.totalSessions,
      remainingSessions: appointment.remainingSessions,
      status: appointment.status
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingAppointment(null);
    setSelectedDate(null);
    setFormData({
      customerId: '',
      serviceId: '',
      appointmentDate: '',
      agreedPrice: '',
      totalSessions: 1,
      remainingSessions: 1,
      status: 'Scheduled'
    });
  };

  // Form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.customerId || !formData.serviceId || !formData.appointmentDate || !formData.agreedPrice) {
      alert('All required fields must be filled');
      return;
    }

    try {
      const appointmentData = {
        customerId: formData.customerId,
        serviceId: formData.serviceId,
        appointmentDate: formData.appointmentDate,
        agreedPrice: formData.agreedPrice,
        totalSessions: formData.totalSessions,
        remainingSessions: formData.remainingSessions,
        status: formData.status
      };

      if (editingAppointment) {
        await appointmentService.update(editingAppointment.appointmentId, appointmentData);
      } else {
        await appointmentService.create(appointmentData);
      }
      
      await fetchData();
      closeModal();
    } catch (error) {
      console.error('Randevu kaydederken hata:', error);
      alert('Randevu kaydedilirken bir hata oluştu');
    }
  };

  // Randevu sil
  const handleDelete = async (appointmentId) => {
    if (!window.confirm('Bu randevuyu silmek istediğinizden emin misiniz?')) {
      return;
    }

    try {
      await appointmentService.delete(appointmentId);
      await fetchData();
    } catch (error) {
      console.error('Randevu silerken hata:', error);
      alert('Randevu silinirken bir hata oluştu');
    }
  };

  // Hizmet seçildiğinde fiyatı otomatik doldur
  const handleServiceChange = (serviceId) => {
    const service = services.find(s => s.serviceId === parseInt(serviceId));
    setFormData({
      ...formData,
      serviceId: serviceId,
      agreedPrice: service ? service.price.toString() : ''
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = statusOptions.find(s => s.value === status);
    return statusConfig ? statusConfig : { label: status, color: 'bg-gray-100 text-gray-800' };
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-rose-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading appointments...</p>
        </div>
      </div>
    );
  }

  const calendarDays = getCalendarDays();

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => window.history.back()}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ArrowLeft className="h-5 w-5 text-gray-600" />
              </button>
              <h1 className="text-2xl font-bold text-gray-900">Beauty Salon</h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-gray-600">Admin</span>
              <button className="px-4 py-2 text-gray-600 hover:text-gray-900 transition-colors">
                Log Out
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6 flex justify-between items-center">
          <h2 className="text-3xl font-bold text-gray-900">Appointments</h2>
          <button
            onClick={() => openAddModal()}
            className="px-4 py-2 bg-rose-500 text-white rounded-lg hover:bg-rose-600 flex items-center space-x-2 transition-colors"
          >
            <Plus className="h-4 w-4" />
            <span>Add Appointment</span>
          </button>
        </div>

        {/* Calendar */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          {/* Calendar Header */}
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-lg font-semibold text-gray-900">
              {monthNames[currentDate.getMonth()]} {currentDate.getFullYear()}
            </h3>
            <div className="flex space-x-2">
              <button
                onClick={() => navigateMonth(-1)}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ArrowLeft className="h-4 w-4 text-gray-600" />
              </button>
              <button
                onClick={() => navigateMonth(1)}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ArrowRight className="h-4 w-4 text-gray-600" />
              </button>
            </div>
          </div>

          {/* Calendar Grid */}
          <div className="grid grid-cols-7 gap-1">
            {/* Weekday Headers */}
            {weekdays.map(day => (
              <div key={day} className="p-3 text-center text-sm font-medium text-gray-500">
                {day}
              </div>
            ))}

            {/* Calendar Days */}
            {calendarDays.map((dayInfo, index) => {
              const dayAppointments = getAppointmentsForDay(dayInfo.date);
              
              return (
                <div
                  key={index}
                  className={`min-h-24 p-2 border border-gray-100 cursor-pointer transition-colors
                    ${!dayInfo.isCurrentMonth ? 'bg-gray-50 text-gray-400' : 'hover:bg-gray-50'}
                    ${dayInfo.isToday ? 'bg-blue-50 border-blue-200' : ''}
                  `}
                  onClick={() => dayInfo.isCurrentMonth && openAddModal(dayInfo.date)}
                >
                  <div className={`text-sm font-medium mb-1 
                    ${dayInfo.isToday ? 'text-blue-600' : dayInfo.isCurrentMonth ? 'text-gray-900' : 'text-gray-400'}
                  `}>
                    {dayInfo.date.getDate()}
                  </div>
                  
                  {/* Appointments for this day */}
                  <div className="space-y-1">
                    {dayAppointments.slice(0, 2).map((apt) => {
                      const customer = customers.find(c => c.customerId === apt.customerId);
                      const time = new Date(apt.appointmentDate).toLocaleTimeString('en-US', 
                        { hour: '2-digit', minute: '2-digit', hour12: false }
                      );
                      const statusBadge = getStatusBadge(apt.status);
                      
                      return (
                        <div
                          key={apt.appointmentId}
                          className="text-xs p-1 rounded cursor-pointer group relative"
                          style={{ backgroundColor: statusBadge.color.includes('blue') ? '#dbeafe' : 
                                   statusBadge.color.includes('green') ? '#d1fae5' :
                                   statusBadge.color.includes('red') ? '#fee2e2' :
                                   statusBadge.color.includes('orange') ? '#fed7aa' : '#f3f4f6' }}
                          onClick={(e) => {
                            e.stopPropagation();
                            openEditModal(apt);
                          }}
                        >
                          <div className="font-medium truncate">
                            {customer?.fullName || 'Unknown'}
                          </div>
                          <div className="text-gray-600 truncate">
                            {time}
                          </div>
                          
                          {/* Hover actions */}
                          <div className="absolute top-0 right-0 opacity-0 group-hover:opacity-100 transition-opacity">
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                handleDelete(apt.appointmentId);
                              }}
                              className="p-1 bg-red-500 text-white rounded-full hover:bg-red-600"
                            >
                              <Trash2 className="h-3 w-3" />
                            </button>
                          </div>
                        </div>
                      );
                    })}
                    
                    {dayAppointments.length > 2 && (
                      <div className="text-xs text-gray-500 font-medium">
                        +{dayAppointments.length - 2} more
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </main>

      {/* Appointment Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg w-full max-w-md max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <h3 className="text-lg font-semibold mb-4">
                {editingAppointment ? 'Edit Appointment' : 'Add New Appointment'}
              </h3>
              
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Customer *
                  </label>
                  <select
                    required
                    value={formData.customerId}
                    onChange={(e) => setFormData({ ...formData, customerId: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    <option value="">Select Customer</option>
                    {customers.map(customer => (
                      <option key={customer.customerId} value={customer.customerId}>
                        {customer.fullName}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Service *
                  </label>
                  <select
                    required
                    value={formData.serviceId}
                    onChange={(e) => handleServiceChange(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    <option value="">Select Service</option>
                    {services.map(service => (
                      <option key={service.serviceId} value={service.serviceId}>
                        {service.serviceName} - ₺{service.price}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Date & Time *
                  </label>
                  <input
                    type="datetime-local"
                    required
                    value={formData.appointmentDate}
                    onChange={(e) => setFormData({ ...formData, appointmentDate: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Agreed Price (₺) *
                  </label>
                  <input
                    type="number"
                    step="0.01"
                    required
                    value={formData.agreedPrice}
                    onChange={(e) => setFormData({ ...formData, agreedPrice: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Total Sessions
                    </label>
                    <input
                      type="number"
                      min="1"
                      value={formData.totalSessions}
                      onChange={(e) => setFormData({ 
                        ...formData, 
                        totalSessions: parseInt(e.target.value),
                        remainingSessions: editingAppointment ? formData.remainingSessions : parseInt(e.target.value)
                      })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                    />
                  </div>

                  {editingAppointment && (
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Remaining Sessions
                      </label>
                      <input
                        type="number"
                        min="0"
                        max={formData.totalSessions}
                        value={formData.remainingSessions}
                        onChange={(e) => setFormData({ ...formData, remainingSessions: parseInt(e.target.value) })}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                      />
                    </div>
                  )}
                </div>

                {editingAppointment && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Status
                    </label>
                    <select
                      value={formData.status}
                      onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                    >
                      {statusOptions.map(option => (
                        <option key={option.value} value={option.value}>
                          {option.label}
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600 transition-colors"
                  >
                    {editingAppointment ? 'Update' : 'Save'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AppointmentsPage;
