import React, { useState, useEffect } from 'react';
import Dashboard from './pages/Dashboard';
import ClientsPage from './pages/ClientsPage';
import AppointmentsPage from './pages/AppointmentsPage';
import PaymentsPage from './pages/PaymentsPage';

const App = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');

  const [customers, setCustomers] = useState([]);
  const [appointments, setAppointments] = useState([]);


  useEffect(() => {
    // Mock customers
    setCustomers([
      { customerId: 1, fullName: 'Emma Johnson', phoneNumber: '0532 123 45 67', notes: 'VIP customer' },
      { customerId: 2, fullName: 'Olivia Williams', phoneNumber: '0535 987 65 43', notes: '' },
      { customerId: 3, fullName: 'Sophia Brown', phoneNumber: '0533 456 78 90', notes: 'Allergic to certain products' },
      { customerId: 4, fullName: 'Isabella Miller', phoneNumber: '0534 789 12 34', notes: '' }
    ]);

    // Mock appointments
    setAppointments([
      {
        appointmentId: 1,
        customerId: 1,
        customer: { fullName: 'Sophia Brown' },
        appointmentDate: '2024-04-01T13:00:00',
        service: { serviceName: 'Haircut' }
      },
      {
        appointmentId: 2,
        customerId: 2,
        customer: { fullName: 'Emma Johnson' },
        appointmentDate: '2024-04-09T14:00:00',
        service: { serviceName: 'Massage' }
      },
      {
        appointmentId: 3,
        customerId: 3,
        customer: { fullName: 'Olivia Williams' },
        appointmentDate: '2024-04-17T09:00:00',
        service: { serviceName: 'Facial' }
      }
    ]);

    // Mock services

  }, []);

  return (
    <>
      {currentPage === 'dashboard' && (
        <Dashboard onNavigate={setCurrentPage} />
      )}

      {currentPage === 'clients' && (
        <ClientsPage
          customers={customers}
          setCustomers={setCustomers}
        />
      )}

      {currentPage === 'appointments' && (
        <AppointmentsPage
          appointments={appointments}
          // setAppointments={setAppointments} // ileride form eklersek kullanacağız
        />
      )}

      {currentPage === 'payments' && <PaymentsPage />}
    </>
  );
};

export default App;
