import React from 'react';
import { ArrowLeft, ArrowRight } from 'lucide-react';

const Calendar = () => {
    const monthNames = ["January", "February", "March", "April", "May", "June",
      "July", "August", "September", "October", "November", "December"
    ];
    
    const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    
    const firstDayOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
    const lastDayOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 0);
    const startDate = new Date(firstDayOfMonth);
    startDate.setDate(startDate.getDate() - firstDayOfMonth.getDay());
    
    const days = [];
    for (let i = 0; i < 42; i++) {
      const date = new Date(startDate);
      date.setDate(startDate.getDate() + i);
      days.push(date);
    }

    const getAppointmentsForDay = (date) => {
      return appointments.filter(apt => {
        const aptDate = new Date(apt.appointmentDate);
        return aptDate.toDateString() === date.toDateString();
      });
    };

    const prevMonth = () => {
      setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1));
    };

    const nextMonth = () => {
      setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1));
    };

    return (
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-lg font-semibold">
            {monthNames[currentMonth.getMonth()]} {currentMonth.getFullYear()}
          </h3>
          <div className="flex space-x-2">
            <button onClick={prevMonth} className="p-2 hover:bg-gray-100 rounded">
              <ArrowLeft className="h-4 w-4" />
            </button>
            <button onClick={nextMonth} className="p-2 hover:bg-gray-100 rounded">
              <ArrowRight className="h-4 w-4" />
            </button>
          </div>
        </div>
        
        <div className="grid grid-cols-7 gap-1">
          {weekdays.map(day => (
            <div key={day} className="p-3 text-center text-sm font-medium text-gray-500">
              {day}
            </div>
          ))}
          
          {days.map((date, index) => {
            const isCurrentMonth = date.getMonth() === currentMonth.getMonth();
            const dayAppointments = getAppointmentsForDay(date);
            
            return (
              <div key={index} className={`p-2 h-24 border border-gray-100 ${!isCurrentMonth ? 'bg-gray-50' : ''}`}>
                <div className={`text-sm ${!isCurrentMonth ? 'text-gray-400' : 'text-gray-900'}`}>
                  {date.getDate()}
                </div>
                {dayAppointments.map(apt => (
                  <div key={apt.appointmentId} className="text-xs bg-rose-100 text-rose-800 px-1 py-0.5 rounded mt-1 truncate">
                    {apt.customer.fullName}
                    <br />
                    {new Date(apt.appointmentDate).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })}
                  </div>
                ))}
              </div>
            );
          })}
        </div>
      </div>
    );
  };


export default Calendar;
