import React, { useState, useEffect } from 'react';
import { customerService, appointmentService, paymentService } from '../api/api';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalClients: 0,
    todayAppointments: 0,
    monthlyRevenue: 0,
    isLoading: true
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // API'den verileri çek
      const [customersRes, appointmentsRes, paymentsRes] = await Promise.all([
        customerService.getAll(),
        appointmentService.getAll(),
        paymentService.getAll()
      ]);

      // Bugünün randevularını filtrele
      const today = new Date().toDateString();
      const todayAppointments = appointmentsRes.data.filter(apt => 
        new Date(apt.appointmentDate).toDateString() === today
      );

      // Bu ayın ödemelerini hesapla
      const currentMonth = new Date().getMonth();
      const currentYear = new Date().getFullYear();
      const monthlyPayments = paymentsRes.data.filter(payment => {
        const paymentDate = new Date(payment.paymentDate);
        return paymentDate.getMonth() === currentMonth && 
               paymentDate.getFullYear() === currentYear &&
               payment.status === 'Paid';
      });

      const monthlyRevenue = monthlyPayments.reduce((sum, payment) => 
        sum + payment.amountPaid, 0
      );

      setStats({
        totalClients: customersRes.data.length,
        todayAppointments: todayAppointments.length,
        monthlyRevenue: monthlyRevenue,
        isLoading: false
      });
    } catch (error) {
      console.error('Dashboard verilerini yüklerken hata:', error);
      setStats(prev => ({ ...prev, isLoading: false }));
    }
  };

  const StatCard = ({ icon, title, value, color }) => (
    <div style={{
      backgroundColor: 'white',
      borderRadius: '12px',
      padding: '24px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
      display: 'flex',
      alignItems: 'center',
      gap: '20px',
      transition: 'transform 0.2s',
      cursor: 'pointer'
    }}
    onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-4px)'}
    onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
    >
      <div style={{
        width: '60px',
        height: '60px',
        backgroundColor: color + '20',
        borderRadius: '12px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '28px'
      }}>
        {icon}
      </div>
      <div>
        <p style={{ 
          margin: '0 0 8px 0', 
          color: '#666', 
          fontSize: '14px' 
        }}>
          {title}
        </p>
        <p style={{ 
          margin: 0, 
          fontSize: '28px', 
          fontWeight: 'bold', 
          color: '#333' 
        }}>
          {value}
        </p>
      </div>
    </div>
  );

  if (stats.isLoading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '400px' 
      }}>
        <p style={{ color: '#666' }}>Yükleniyor...</p>
      </div>
    );
  }

  return (
    <div>
      <h1 style={{ 
        fontSize: '32px', 
        fontWeight: 'bold', 
        color: '#333', 
        marginBottom: '32px' 
      }}>
        Dashboard
      </h1>

      {/* İstatistik Kartları */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '24px',
        marginBottom: '40px'
      }}>
        <StatCard
          icon="👥"
          title="Toplam Müşteri"
          value={stats.totalClients}
          color="#4F46E5"
        />
        <StatCard
          icon="📅"
          title="Bugünün Randevuları"
          value={stats.todayAppointments}
          color="#10B981"
        />
        <StatCard
          icon="💰"
          title="Aylık Gelir"
          value={`₺${stats.monthlyRevenue.toLocaleString('tr-TR')}`}
          color="#F59E0B"
        />
      </div>

      {/* Hızlı İşlemler */}
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '24px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
      }}>
        <h2 style={{ 
          fontSize: '20px', 
          fontWeight: 'bold', 
          marginBottom: '20px',
          color: '#333'
        }}>
          Hızlı İşlemler
        </h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '16px'
        }}>
          <button style={{
            padding: '16px',
            backgroundColor: '#4F46E5',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            cursor: 'pointer',
            transition: 'opacity 0.2s'
          }}
          onMouseEnter={(e) => e.target.style.opacity = '0.9'}
          onMouseLeave={(e) => e.target.style.opacity = '1'}
          >
            + Yeni Müşteri
          </button>
          <button style={{
            padding: '16px',
            backgroundColor: '#10B981',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            cursor: 'pointer',
            transition: 'opacity 0.2s'
          }}
          onMouseEnter={(e) => e.target.style.opacity = '0.9'}
          onMouseLeave={(e) => e.target.style.opacity = '1'}
          >
            + Yeni Randevu
          </button>
          <button style={{
            padding: '16px',
            backgroundColor: '#F59E0B',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            cursor: 'pointer',
            transition: 'opacity 0.2s'
          }}
          onMouseEnter={(e) => e.target.style.opacity = '0.9'}
          onMouseLeave={(e) => e.target.style.opacity = '1'}
          >
            + Ödeme Kaydet
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
