import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Component imports
import Layout from './components/Layout';
import Dashboard from './pages/Dashboard';
import Customers from './pages/Customers';
import Appointments from './pages/Appointments';
import Services from './pages/Services';

// Global styles
const globalStyles = {
  fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
  margin: 0,
  padding: 0,
  boxSizing: 'border-box'
};

const App = () => {
  // Global stil uygulama
  React.useEffect(() => {
    // Body stillerini ayarla
    document.body.style.margin = '0';
    document.body.style.padding = '0';
    document.body.style.fontFamily = globalStyles.fontFamily;
    document.body.style.backgroundColor = '#f5f5f5';

    // Box-sizing için global stil
    const style = document.createElement('style');
    style.textContent = '* { box-sizing: border-box; }';
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <Router>
      <div style={globalStyles}>
        <Layout>
          <Routes>
            {/* Ana sayfa - Dashboard */}
            <Route path="/" element={<Dashboard />} />

            {/* Müşteriler */}
            <Route path="/customers" element={<Customers />} />

            {/* Randevular */}
            <Route path="/appointments" element={<Appointments />} />

            {/* Hizmetler */}
            <Route path="/services" element={<Services />} />

            {/* Payments rotası eklenebilir */}
            {/* <Route path="/payments" element={<Payments />} /> */}

            {/* 404 - Bulunamayan sayfalar için ana sayfaya yönlendir */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </Layout>
      </div>
    </Router>
  );
};

export default App;
