import { useEffect, useState } from 'react';
import api from '../api/api';

function AppointmentList() {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await api.get('/appointments');
        setAppointments(res.data);
      } catch (err) {
        console.error('Randevular alınamadı', err);
      }
    };
    fetchAppointments();
  }, []);

  return (
    <div>
      <h2>Randevu Listesi</h2>
      {appointments.length === 0 ? (
        <p>Hiç randevu bulunamadı.</p>
      ) : (
        <table border='1' cellPadding='8' cellSpacing='0'>
          <thead>
            <tr>
              <th>Müşteri</th>
              <th>Hizmet</th>
              <th>Ücret</th>
              <th>Ödendi</th>
              <th>Seans</th>
              <th>Ödeme</th>
              <th>Durum</th>
              <th>Geldiği Tarih</th>
              <th>Gelecek Tarih</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((a) => (
              <tr key={a.appointmentId}>
                <td>{a.customer?.fullName}</td>
                <td>{a.service?.serviceName}</td>
                <td>{a.agreedPrice} TL</td>
                <td>{a.paidAmount} TL</td>
                <td>
                  {a.remainingSessions}/{a.totalSessions}
                </td>
                <td>
                  {a.paymentMethod}{' '}
                  {a.installmentCount > 0 && `(${a.installmentCount} taksit)`}
                </td>
                <td>{a.status}</td>
                <td>{a.appointmentDate?.slice(0, 10)}</td>
                <td>{a.nextAppointmentDate?.slice(0, 10)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AppointmentList;
