import React, { useState, useEffect } from 'react';
import { Plus, ArrowLeft, Search, Filter, Eye, Edit, Trash2, CreditCard } from 'lucide-react';
import { paymentService, customerService, appointmentService } from '../api/api';

const PaymentsPage = () => {
  const [payments, setPayments] = useState([]);
  const [filteredPayments, setFilteredPayments] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [filterMethod, setFilterMethod] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [showBalanceModal, setShowBalanceModal] = useState(false);
  const [editingPayment, setEditingPayment] = useState(null);
  const [selectedCustomerBalance, setSelectedCustomerBalance] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [formData, setFormData] = useState({
    customerId: '',
    appointmentId: '',
    amountPaid: '',
    paymentMethod: 'Cash',
    paymentDate: new Date().toISOString().slice(0, 16),
    status: 'Paid'
  });

  const paymentMethods = [
    { value: 'Cash', label: 'Cash', icon: '💵' },
    { value: 'CreditCard', label: 'Credit Card', icon: '💳' },
    { value: 'DebitCard', label: 'Debit Card', icon: '💳' },
    { value: 'BankTransfer', label: 'Bank Transfer', icon: '🏦' }
  ];

  const paymentStatuses = [
    { value: 'Pending', label: 'Pending', color: 'bg-yellow-100 text-yellow-800' },
    { value: 'Paid', label: 'Paid', color: 'bg-green-100 text-green-800' },
    { value: 'Cancelled', label: 'Cancelled', color: 'bg-red-100 text-red-800' },
    { value: 'Refunded', label: 'Refunded', color: 'bg-gray-100 text-gray-800' }
  ];

  // Sayfa yüklendiğinde verileri getir
  useEffect(() => {
    fetchData();
  }, []);

  // Filtreleme işlemi
  useEffect(() => {
    filterPayments();
  }, [payments, searchQuery, filterStatus, filterMethod]);

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [paymentsRes, customersRes, appointmentsRes] = await Promise.all([
        paymentService.getAll(),
        customerService.getAll(),
        appointmentService.getAll()
      ]);
      
      setPayments(paymentsRes.data);
      setCustomers(customersRes.data);
      setAppointments(appointmentsRes.data);
    } catch (error) {
      console.error('Veri yüklerken hata:', error);
      alert('Veriler yüklenirken bir hata oluştu');
    } finally {
      setIsLoading(false);
    }
  };

  const filterPayments = () => {
    let filtered = [...payments];

    // Metin arama
    if (searchQuery.trim()) {
      filtered = filtered.filter(payment =>
        payment.customerName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        payment.serviceName?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Durum filtresi
    if (filterStatus) {
      filtered = filtered.filter(payment => payment.status === filterStatus);
    }

    // Ödeme yöntemi filtresi
    if (filterMethod) {
      filtered = filtered.filter(payment => payment.paymentMethod === filterMethod);
    }

    setFilteredPayments(filtered);
  };

  const openAddModal = () => {
    setEditingPayment(null);
    setFormData({
      customerId: '',
      appointmentId: '',
      amountPaid: '',
      paymentMethod: 'Cash',
      paymentDate: new Date().toISOString().slice(0, 16),
      status: 'Paid'
    });
    setShowModal(true);
  };

  const openEditModal = (payment) => {
    setEditingPayment(payment);
    setFormData({
      customerId: payment.customerId.toString(),
      appointmentId: payment.appointmentId?.toString() || '',
      amountPaid: payment.amountPaid.toString(),
      paymentMethod: payment.paymentMethod,
      paymentDate: new Date(payment.paymentDate).toISOString().slice(0, 16),
      status: payment.status
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingPayment(null);
    setFormData({
      customerId: '',
      appointmentId: '',
      amountPaid: '',
      paymentMethod: 'Cash',
      paymentDate: new Date().toISOString().slice(0, 16),
      status: 'Paid'
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.customerId || !formData.amountPaid) {
      alert('Customer and amount fields are required');
      return;
    }

    try {
      const paymentData = {
        customerId: formData.customerId,
        appointmentId: formData.appointmentId || null,
        amountPaid: formData.amountPaid,
        paymentMethod: formData.paymentMethod,
        paymentDate: formData.paymentDate,
        status: formData.status
      };

      if (editingPayment) {
        await paymentService.update(editingPayment.paymentId, paymentData);
      } else {
        await paymentService.create(paymentData);
      }
      
      await fetchData();
      closeModal();
    } catch (error) {
      console.error('Ödeme kaydederken hata:', error);
      alert('Ödeme kaydedilirken bir hata oluştu');
    }
  };

  const handleDelete = async (paymentId) => {
    if (!window.confirm('Bu ödemeyi silmek istediğinizden emin misiniz?')) {
      return;
    }

    try {
      await paymentService.delete(paymentId);
      await fetchData();
    } catch (error) {
      console.error('Ödeme silerken hata:', error);
      alert('Ödeme silinirken bir hata oluştu');
    }
  };

  const showCustomerBalance = async (customerId) => {
    try {
      const response = await paymentService.getCustomerBalance(customerId);
      setSelectedCustomerBalance(response.data);
      setShowBalanceModal(true);
    } catch (error) {
      console.error('Müşteri bakiye bilgisi alınırken hata:', error);
      alert('Müşteri bakiye bilgisi alınamadı');
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = paymentStatuses.find(s => s.value === status);
    return statusConfig || { label: status, color: 'bg-gray-100 text-gray-800' };
  };

  const getMethodIcon = (method) => {
    const methodConfig = paymentMethods.find(m => m.value === method);
    return methodConfig?.icon || '💳';
  };

  const getCustomerAppointments = (customerId) => {
    return appointments.filter(apt => apt.customerId === parseInt(customerId));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-rose-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading payments...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => window.history.back()}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ArrowLeft className="h-5 w-5 text-gray-600" />
              </button>
              <h1 className="text-2xl font-bold text-gray-900">Beauty Salon</h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-gray-600">Admin</span>
              <button className="px-4 py-2 text-gray-600 hover:text-gray-900 transition-colors">
                Log Out
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">Payments</h2>
          
          {/* Search and Filters */}
          <div className="flex flex-col lg:flex-row gap-4 mb-6">
            {/* Search */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search by customer or service..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
              />
            </div>

            {/* Filters */}
            <div className="flex gap-2">
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
              >
                <option value="">All Statuses</option>
                {paymentStatuses.map(status => (
                  <option key={status.value} value={status.value}>
                    {status.label}
                  </option>
                ))}
              </select>

              <select
                value={filterMethod}
                onChange={(e) => setFilterMethod(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
              >
                <option value="">All Methods</option>
                {paymentMethods.map(method => (
                  <option key={method.value} value={method.value}>
                    {method.label}
                  </option>
                ))}
              </select>

              <button
                onClick={openAddModal}
                className="px-4 py-2 bg-rose-500 text-white rounded-lg hover:bg-rose-600 flex items-center space-x-2 transition-colors"
              >
                <Plus className="h-4 w-4" />
                <span>Add Payment</span>
              </button>
            </div>
          </div>
        </div>

        {/* Payments Table */}
        <div className="bg-white rounded-lg shadow-sm overflow-hidden border border-gray-200">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Client
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Service
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Amount
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Date
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Method
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredPayments.length === 0 ? (
                  <tr>
                    <td colSpan="7" className="px-6 py-12 text-center text-gray-500">
                      {searchQuery || filterStatus || filterMethod 
                        ? 'No payments found matching your criteria.' 
                        : 'No payments recorded yet.'}
                    </td>
                  </tr>
                ) : (
                  filteredPayments.map((payment) => {
                    const statusBadge = getStatusBadge(payment.status);
                    
                    return (
                      <tr key={payment.paymentId} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center">
                            <div>
                              <div className="text-sm font-medium text-gray-900">
                                {payment.customerName}
                              </div>
                              <button
                                onClick={() => showCustomerBalance(payment.customerId)}
                                className="text-xs text-rose-600 hover:text-rose-900"
                              >
                                View Balance
                              </button>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {payment.serviceName || 'General Payment'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          ₺{payment.amountPaid.toLocaleString('tr-TR', { minimumFractionDigits: 2 })}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(payment.paymentDate).toLocaleDateString('tr-TR')}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          <div className="flex items-center space-x-2">
                            <span>{getMethodIcon(payment.paymentMethod)}</span>
                            <span>{payment.paymentMethodDisplay}</span>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${statusBadge.color}`}>
                            {statusBadge.label}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <div className="flex justify-end space-x-2">
                            <button
                              onClick={() => openEditModal(payment)}
                              className="text-rose-600 hover:text-rose-900 p-1 hover:bg-rose-50 rounded transition-colors"
                            >
                              <Edit className="h-4 w-4" />
                            </button>
                            <button
                              onClick={() => handleDelete(payment.paymentId)}
                              className="text-red-600 hover:text-red-900 p-1 hover:bg-red-50 rounded transition-colors"
                            >
                              <Trash2 className="h-4 w-4" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>
        </div>
      </main>

      {/* Payment Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg w-full max-w-md max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <h3 className="text-lg font-semibold mb-4">
                {editingPayment ? 'Edit Payment' : 'Add New Payment'}
              </h3>
              
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Customer *
                  </label>
                  <select
                    required
                    value={formData.customerId}
                    onChange={(e) => setFormData({ ...formData, customerId: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    <option value="">Select Customer</option>
                    {customers.map(customer => (
                      <option key={customer.customerId} value={customer.customerId}>
                        {customer.fullName}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Appointment (Optional)
                  </label>
                  <select
                    value={formData.appointmentId}
                    onChange={(e) => setFormData({ ...formData, appointmentId: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    <option value="">General Payment</option>
                    {formData.customerId && getCustomerAppointments(formData.customerId).map(appointment => {
                      const service = appointment.serviceName || 'Unknown Service';
                      const date = new Date(appointment.appointmentDate).toLocaleDateString('tr-TR');
                      return (
                        <option key={appointment.appointmentId} value={appointment.appointmentId}>
                          {service} - {date}
                        </option>
                      );
                    })}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Amount (₺) *
                  </label>
                  <input
                    type="number"
                    step="0.01"
                    required
                    value={formData.amountPaid}
                    onChange={(e) => setFormData({ ...formData, amountPaid: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Payment Method
                  </label>
                  <select
                    value={formData.paymentMethod}
                    onChange={(e) => setFormData({ ...formData, paymentMethod: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    {paymentMethods.map(method => (
                      <option key={method.value} value={method.value}>
                        {method.icon} {method.label}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Payment Date
                  </label>
                  <input
                    type="datetime-local"
                    value={formData.paymentDate}
                    onChange={(e) => setFormData({ ...formData, paymentDate: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Status
                  </label>
                  <select
                    value={formData.status}
                    onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-rose-500 focus:border-transparent"
                  >
                    {paymentStatuses.map(status => (
                      <option key={status.value} value={status.value}>
                        {status.label}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600 transition-colors"
                  >
                    {editingPayment ? 'Update' : 'Save'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Customer Balance Modal */}
      {showBalanceModal && selectedCustomerBalance && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg w-full max-w-lg">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">Customer Balance</h3>
                <button
                  onClick={() => setShowBalanceModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  ✕
                </button>
              </div>
              
              <div className="space-y-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="font-medium text-gray-900 mb-2">
                    {selectedCustomerBalance.customerName}
                  </h4>
                  
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-gray-600">Total Agreed:</span>
                      <p className="font-medium">₺{selectedCustomerBalance.totalAgreedAmount.toLocaleString('tr-TR')}</p>
                    </div>
                    <div>
                      <span className="text-gray-600">Total Paid:</span>
                      <p className="font-medium text-green-600">₺{selectedCustomerBalance.totalPaidAmount.toLocaleString('tr-TR')}</p>
                    </div>
                    <div>
                      <span className="text-gray-600">Pending:</span>
                      <p className="font-medium text-yellow-600">₺{selectedCustomerBalance.pendingAmount.toLocaleString('tr-TR')}</p>
                    </div>
                    <div>
                      <span className="text-gray-600">Remaining Debt:</span>
                      <p className="font-medium text-red-600">₺{selectedCustomerBalance.remainingDebt.toLocaleString('tr-TR')}</p>
                    </div>
                  </div>
                  
                  {selectedCustomerBalance.overPaid > 0 && (
                    <div className="mt-2 p-2 bg-blue-50 rounded">
                      <span className="text-sm text-blue-600">
                        Credit: ₺{selectedCustomerBalance.overPaid.toLocaleString('tr-TR')}
                      </span>
                    </div>
                  )}
                  
                  <div className="mt-4 text-xs text-gray-500">
                    <p>Total Payments: {selectedCustomerBalance.totalPayments}</p>
                    {selectedCustomerBalance.lastPaymentDate && (
                      <p>Last Payment: {new Date(selectedCustomerBalance.lastPaymentDate).toLocaleDateString('tr-TR')}</p>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PaymentsPage;
