// src/pages/ClientsPage.jsx
import React, { useState } from 'react';
import Header from '../components/Header';
import CustomerForm from '../components/Customer/CustomerForm';
import CustomerTable from '../components/Customer/CustomerTable';
import { Plus, Search } from 'lucide-react';

const ClientsPage = ({ customers, setCustomers }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddCustomer, setShowAddCustomer] = useState(false);
  const [newCustomer, setNewCustomer] = useState({ fullName: '', phoneNumber: '', notes: '' });
  const [editingCustomer, setEditingCustomer] = useState(null);

  const filteredCustomers = customers.filter(customer =>
    customer.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    customer.phoneNumber.includes(searchTerm)
  );

  const handleAddCustomer = () => {
    if (newCustomer.fullName && newCustomer.phoneNumber) {
      const newId = customers.length > 0 ? Math.max(...customers.map(c => c.customerId)) + 1 : 1;
      setCustomers([...customers, { customerId: newId, ...newCustomer }]);
      setNewCustomer({ fullName: '', phoneNumber: '', notes: '' });
      setShowAddCustomer(false);
    }
  };

  const handleUpdateCustomer = () => {
    setCustomers(customers.map(c =>
      c.customerId === editingCustomer.customerId ? editingCustomer : c
    ));
    setEditingCustomer(null);
  };

  const handleDeleteCustomer = (id) => {
    setCustomers(customers.filter(c => c.customerId !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="mb-6">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">Clients</h2>

          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search clients..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-rose-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <button
              onClick={() => setShowAddCustomer(true)}
              className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600 flex items-center whitespace-nowrap"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Client
            </button>
          </div>
        </div>

        <CustomerTable
          customers={filteredCustomers}
          onEdit={setEditingCustomer}
          onDelete={handleDeleteCustomer}
        />
      </div>

      {showAddCustomer && (
        <CustomerForm
          customer={newCustomer}
          onSave={handleAddCustomer}
          onCancel={() => {
            setShowAddCustomer(false);
            setNewCustomer({ fullName: '', phoneNumber: '', notes: '' });
          }}
          onChange={setNewCustomer}
          title="Add New Client"
        />
      )}

      {editingCustomer && (
        <CustomerForm
          customer={editingCustomer}
          onSave={handleUpdateCustomer}
          onCancel={() => setEditingCustomer(null)}
          onChange={setEditingCustomer}
          title="Edit Client"
        />
      )}
    </div>
  );
};

export default ClientsPage;
