// src/pages/AppointmentsPage.jsx
import React, { useState } from 'react';
import Header from '../components/Header';
import Calendar from '../components/Appointment/Calendar';
import { Plus } from 'lucide-react';

const AppointmentsPage = ({ appointments }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const handleAddAppointment = () => {
    alert('Appointment form to be implemented');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="mb-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-3xl font-bold text-gray-900">Appointments</h2>
            <button
              onClick={handleAddAppointment}
              className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600 flex items-center"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Appointment
            </button>
          </div>
        </div>

        <Calendar
          currentMonth={currentMonth}
          setCurrentMonth={setCurrentMonth}
          appointments={appointments}
        />
      </div>
    </div>
  );
};

export default AppointmentsPage;
